workflow-item.script:
// Inputs: vm (VC:VirtualMachine), newPassword (String)
var adminUser = "Administrator";
System.log("Attempting to reset password for VM: " + vm.name);

var guestAuth = new VcNamePasswordAuthentication();
guestAuth.username = adminUser;
guestAuth.password = vm.guest.guestIdentity.userName == adminUser ? vm.guest.guestIdentity.password : ""; 
// If you don’t have the current password stored, you might need Guest Operations with elevated privileges

var mgr = vm.sdkConnection.guestOperationsManager;
var procMgr = mgr.processManager;

var scriptSpec = new VcGuestProgramSpec();
scriptSpec.programPath = "cmd.exe";
scriptSpec.arguments = "/c net user " + adminUser + " \"" + newPassword + "\"";

System.log("Running guest operation to reset Administrator password...");
procMgr.startProgramInGuest(vm, guestAuth, scriptSpec);
System.log("Password reset command sent to VM: " + vm.name);
